     * @param amounts - 각 주소가 받을 토큰 양의 배열
     */
    function batchTransfer(address[] calldata recipients, uint256[] calldata amounts) external {
        require(
            gameContracts[msg.sender] || gameManagers[msg.sender] || msg.sender == owner(),
            "TesoToken: Only authorized game contracts or managers"
        );
        require(recipients.length == amounts.length, "TesoToken: Arrays length mismatch");
        require(recipients.length > 0, "TesoToken: Empty arrays");
        
        uint256 totalAmount = 0;
        
        // 총 전송량 계산
        for (uint256 i = 0; i < amounts.length; i++) {
            totalAmount += amounts[i];
        }
        
        // 발신자 잔액 확인
        require(balanceOf(msg.sender) >= totalAmount, "TesoToken: Insufficient balance");
        
        // 배치 전송 실행
        for (uint256 i = 0; i < recipients.length; i++) {
            require(recipients[i] != address(0), "TesoToken: Invalid recipient address");
            require(amounts[i] > 0, "TesoToken: Amount must be greater than 0");
            
            _transfer(msg.sender, recipients[i], amounts[i]);
        }
        
        emit BatchTransferCompleted(totalAmount, recipients.length);
    }

    /**
     * 토큰 민팅 (소유자만 실행 가능)
     * 
     * @param to - 토큰을 받을 주소
     * @param amount - 민팅할 토큰 양
     */
    function mint(address to, uint256 amount) external onlyOwner {
        require(to != address(0), "TesoToken: Invalid address");
        require(amount > 0, "TesoToken: Amount must be greater than 0");
        
        _mint(to, amount);
    }

    /**
     * 토큰 소각 (소유자만 실행 가능)
     * 
     * @param amount - 소각할 토큰 양
     */
    function burn(uint256 amount) external onlyOwner {
        require(amount > 0, "TesoToken: Amount must be greater than 0");
        require(balanceOf(msg.sender) >= amount, "TesoToken: Insufficient balance");
        
        _burn(msg.sender, amount);
    }

    /**
     * 특정 주소의 토큰 소각 (소유자만 실행 가능)
     * 
     * @param account - 토큰을 소각할 주소
     * @param amount - 소각할 토큰 양
     */
    function burnFrom(address account, uint256 amount) external onlyOwner {
        require(account != address(0), "TesoToken: Invalid address");
        require(amount > 0, "TesoToken: Amount must be greater than 0");
        require(balanceOf(account) >= amount, "TesoToken: Insufficient balance");
        
        _burn(account, amount);
    }

    /**
     * 게임 컨트랙트인지 확인
     * 
     * @param contractAddress - 확인할 컨트랙트 주소
     * @return 게임 컨트랙트 여부
     */
    function isGameContract(address contractAddress) external view returns (bool) {
        return gameContracts[contractAddress];
    }

    /**
     * 게임 매니저인지 확인
     * 
     * @param manager - 확인할 매니저 주소
     * @return 게임 매니저 여부
     */
    function isGameManager(address manager) external view returns (bool) {
        return gameManagers[manager];
    }

    /**
     * 총 공급량 조회
     * 
     * @return 현재 총 토큰 공급량
     */
    function totalSupply() public view override returns (uint256) {
        return super.totalSupply();
    }

    /**
     * 특정 주소의 잔액 조회
     * 
     * @param account - 조회할 주소
     * @return 해당 주소의 토큰 잔액
     */
    function balanceOf(address account) public view override returns (uint256) {
        return super.balanceOf(account);
    }
}

